  //   }

  //   axios.post('http://localhost:3001/api/putData', {
  //     id: idToBeAdded,
  //     message: message,
  //   });
  // };

  render() {
    const { data } = this.state;
    return (
      <div>
        <BarChart />
        <ul>
          {data.length <= 0
            ? 'DB IS EMPTY'
            : data.map((dat) => (
                <li style={{ padding: '10px' }} key={data.message}>
                  <span style={{ color: 'gray' }}> id: </span> {dat.name} <br />
                  <span style={{ color: 'gray' }}> data: </span>
                  {dat.count}
                </li>
              ))}
        </ul>

      </div>
    )
  }


}

