import React from 'react'
import { defaults, Bar } from 'react-chartjs-2'
import data from "../ticker_data.json"

defaults.global.tooltips.enabled = false
defaults.global.legend.position = 'bottom'

const ticks = data;
const sub_names = ['Investing', 'SecurityAnalysis', 'Finance', 'WallStreetBets', 'Options', 'Stocks', 'StockMarket'];
const time_filters_list = ['hour','day','week','month','year','all'];

class BarChart extends React.Component {

  sub = sub_names[0];
  time = time_filters_list[0];

  constructor(props) {
    super(props);
    this.state = {
      data_arr: getSubAndTime(this.sub, this.time),
      label_arr: ["MSFT", "PLTR", "TSLA", "AAPL", "TWTR", "NFLX", "BABA", "DIS"]
    };
  }

  render() {
    return (
      <div>
        <Bar
          data={{
            labels: this.state.label_arr,
            datasets: [
              {
                label: '# of Mentions',
                data: this.state.data_arr,
                backgroundColor: [
                  'rgba(255, 99, 132, 0.2)',
                  'rgba(54, 162, 235, 0.2)',
                  'rgba(255, 206, 86, 0.2)',
                  'rgba(75, 192, 192, 0.2)',
                  'rgba(153, 102, 255, 0.2)',
                  'rgba(255, 159, 64, 0.2)',
                ],
                borderWidth: 1,
              },
            ],
          }}
          height={400}
          width={600}
          options={{
            maintainAspectRatio: false,
            title: {
              display: true,
              text: "r/" + this.sub + "(" + this.time + ")",
              fontSize: 25
            },
            scales: {
              yAxes: [
                {
                  ticks: {
                    beginAtZero: true,
                  },
                },
              ],
            },
            legend: {
              labels: {
                boxWidth: 0,
                fontSize: 15,
              },
            },
          }}
        />
      </div>
    )    
  }
}

