import React from 'react'
import { Pie, defaults, Bar } from 'react-chartjs-2'
import data from "../ticker_data.json"

defaults.global.tooltips.enabled = false
defaults.global.legend.position = 'bottom'

const ticks = data;

var investing_all = []

const ticker_labels = ["MSFT", "PLTR", "TSLA", "AAPL", "TWTR", "NFLX", "BABA", "DIS"];

for (let i = 0; i < ticks.tickers.length; i++){
  if (ticks.tickers[i].time_period === "all" && ticks.tickers[i].subreddit === "Investing"){
    investing_all.push(ticks.tickers[i].mentions);
  }
}


class BarChart extends React.Component {
  render() {
    return (
      <div>
        <Bar
          data={{
            labels: ticker_labels,
            datasets: [
              {
                label: '# of Mentions',
                data: investing_all,
                backgroundColor: [
                  'rgba(255, 99, 132, 0.2)',
                  'rgba(54, 162, 235, 0.2)',
                  'rgba(255, 206, 86, 0.2)',
                  'rgba(75, 192, 192, 0.2)',
                  'rgba(153, 102, 255, 0.2)',
                  'rgba(255, 159, 64, 0.2)',
                ],
                borderWidth: 1,
              },
            ],
          }}
          height={400}
          width={600}
          options={{
            maintainAspectRatio: false,
            title: {
              display: true,
              text: "r/Investing (All-time)"
            },
            scales: {
              yAxes: [
                {
                  ticks: {
                    beginAtZero: true,
                  },
                },
              ],
            },
            legend: {
              labels: {
                boxWidth: 0,
                fontSize: 15,
              },
            },
          }}
        />
      </div>
    )    
  }
}
