import React from 'react'
import { defaults, Bar } from 'react-chartjs-2'
import data from "../ticker_data.json"

defaults.global.tooltips.enabled = false
defaults.global.legend.position = 'bottom'

const ticks = data;
const sub_names = ['Investing', 'SecurityAnalysis', 'Finance', 'WallStreetBets', 'Options', 'Stocks', 'StockMarket'];
const time_filters_list = ['hour','day','week','month','year','all'];

class BarChart extends React.Component {

  sub = sub_names[3];
  time = time_filters_list[4];

  constructor(props) {
    super(props);
    this.state = {
      data_arr: [],
      label_arr: ["MSFT", "PLTR", "TSLA", "AAPL", "TWTR", "NFLX", "BABA", "DIS"]
    };
  }

  componentDidMount() {
    this.change0();
  }

  change0() {
    this.setState({ data_arr: getSubAndTime("WallStreetBets", "all")})
  }

  // handleCallback = (childData) => {
  //   this.setState({data_arr: childData})
  // }

  render() {
    const {data} = this.state;
    return (
      <div>
        <Bar
          data={{
            labels: this.state.label_arr,
            datasets: [
              {
                label: '# of Mentions',
                data: this.state.data_arr,
                backgroundColor: [
                  'rgba(255, 99, 132, 0.2)',
                  'rgba(54, 162, 235, 0.2)',
                  'rgba(255, 206, 86, 0.2)',
                  'rgba(75, 192, 192, 0.2)',
                  'rgba(153, 102, 255, 0.2)',
                  'rgba(255, 159, 64, 0.2)',
                ],
                borderWidth: 1,
              },
            ],
          }}
          height={400}
          width={600}
          options={{
            maintainAspectRatio: false,
            responsive: true,
            title: {
              display: true,
              text: "r/" + this.sub + " (" + this.time + ")",
              fontSize: 25
            },
            scales: {
              yAxes: [
                {
                  ticks: {
                    beginAtZero: true,
                  },
                },
              ],
            },
            legend: {
              labels: {
                boxWidth: 0,
                fontSize: 15,
              },
            },
          }}
        />
        <UserInputForm />
      </div>
    )    
  }
}

class UserInputForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sub_name: '',
      time_duration: ''
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    const {sub_name, time_duration} = this.state;
    // this.props.parentCallback("Data from child");
    event.preventDefault();
    alert(`
      ____Your Details____\n
      SubName : ${sub_name}
      TimePeriod : ${time_duration}
    `)
  }

  handleChange(event) {
    this.setState({
      [event.target.name] : event.target.value
    })
  }

  render() {
    return(
      <form onSubmit={this.handleSubmit}>
        <div>
          <label htmlFor="sub_name">Sub Name</label>
          <input 
            name="sub_name"
            placeholder="Investing"
            value = {this.state.sub_name}
            onChange={this.handleChange}
            />
        </div>
        <div>
          <label htmlFor="time_duration">Time Period</label>
          <input
            name="time_duration"
            placeholder="All-Time"
            value={this.state.time_duration}
            onChange={this.handleChange}
            />
        </div>
        <div>
          <button>ReRender Chart</button>
        </div>
      </form>
    )
  }
}



function getSubAndTime(input_subreddit, input_time_period){
  var ticker_mentions = [];
  for (let i = 0; i < ticks.tickers.length; i++){
    if (ticks.tickers[i].time_period === input_time_period && ticks.tickers[i].subreddit === input_subreddit){
      ticker_mentions.push(ticks.tickers[i].mentions);
    }
  }
  return ticker_mentions;  
}

export default BarChart
