import React from 'react'


class UserInputForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        sub_name: '',
        time_duration: ''
      }
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
      const {sub_name, time_duration} = this.state;
      event.preventDefault();
      alert('SubName: ${sub_name}  TimePeriod: ${time_duration} ')
    }

    handleChange(event) {
      this.setState({
        [event.target.name] : event.target.value
      })
    }

    render() {
      return(
        <form onSubmit={this.handleSubmit}>
          <div>
            <label htmlFor="sub_name">Sub Name</label>
            <input 
              name="sub_name"
              placeholder="Investing"
              value = {this.state.sub_name}
              onChange={this.handleChange}
              />
          </div>
          <div>
            <label htmlFor="time_duration">Time Period</label>
            <input
              name="time_duration"
              placeholder="All-Time"
              value={this.state.time_duration}
              onChange={this.handleChange}
              />
          </div>
          <div>
            <button>ReRender Chart</button>
          </div>
        </form>
      )
    }
  }

export default UserInputForm
