import React from 'react'
import { Pie, defaults, Bar } from 'react-chartjs-2'
import data from "../ticker_data.json"

defaults.global.tooltips.enabled = false
defaults.global.legend.position = 'bottom'

const ticks = data;
// console.log(ticks.tickers);

// for(let i = 0; i < ticks.tickers.length; i++){
//   console.log(ticks.tickers[i]);
// }

var investing_all = []

const labels = ["MSFT", "PLTR", "TSLA", "AAPL", "TWTR", "NFLX", "BABA", "DIS"];

for (let i = 0; i < ticks.tickers.length; i++){
  if (ticks.tickers[i].time_period === "all" && ticks.tickers[i].subreddit === "Investing"){
    investing_all.push(ticks.tickers[i].mentions);
  }
}

console.log(investing_all);

const BarChart = () => {
  return (
    <div>
      <Bar
        data={{
          labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
          datasets: [
            {
              label: '# of Mentions',
              data: [12, 19, 3, 5, 2, 3],
              // backgroundColor: [
              //   'rgba(255, 99, 132, 0.2)',
              //   'rgba(54, 162, 235, 0.2)',
              //   'rgba(255, 206, 86, 0.2)',
              //   'rgba(75, 192, 192, 0.2)',
              //   'rgba(153, 102, 255, 0.2)',
              //   'rgba(255, 159, 64, 0.2)',
              // ],
              // borderColor: [
              //   'rgba(255, 99, 132, 1)',
              //   'rgba(54, 162, 235, 1)',
              //   'rgba(255, 206, 86, 1)',
              //   'rgba(75, 192, 192, 1)',
              //   'rgba(153, 102, 255, 1)',
              //   'rgba(255, 159, 64, 1)',
              // ],
              borderWidth: 1,
            },
            // {
            //   label: 'Quantity',
            //   data: [47, 52, 67, 58, 9, 50],
            //   backgroundColor: 'orange',
            //   borderColor: 'red',
            // },
          ],
        }}
        height={400}
        width={600}
        options={{
          maintainAspectRatio: false,
          scales: {
            yAxes: [
              {
                ticks: {
                  beginAtZero: true,
                },
              },
            ],
          },
          legend: {
            labels: {
              fontSize: 25,
            },
          },
        }}
      />
    </div>
  )
}

export default BarChart
